package com.mimoza_app.notes.campusshop.models

import java.io.Serializable

data class ShopItem(val name: String = "",
                    val price: String = "",
                    val description: String = "",
                    val building: String = "",
                    val category: String = "",
                    val image: String = placeholderBytecode,
                    val uid: String = "") : Serializable
{
    companion object{
        const val placeholderBytecode = ""
    }
}